  `staff_group_id` int(11) NOT NULL AUTO_INCREMENT,
  `staff_group_name` varchar(32) NOT NULL,
  `customer_account_access` tinyint(1) NOT NULL,
  `location_access` tinyint(1) NOT NULL,
